import React, { useEffect, useState } from "react";
import { Button } from "react-bootstrap";

function Frame2A({ num1, denom1, num2, denom2,lcm , setFrameNo}) {
    const [step, setStep] = useState(0);
    useEffect(() => {
      //Implementing the setInterval method
      const interval = setInterval(() => {
        setStep(step + 1);
        if (step >= 20) {
          clearInterval(interval);
        }
      }, 1000);

      //Clearing the interval
      return () => clearInterval(interval);
    }, [step]);

    const renderButtons = () => {
        return (
          <div className="d-flex align-items-evenly w-100">
            <Button variant="primary" onClick={()=>setFrameNo(1)}>
              Back
            </Button>
            <Button variant="primary" onClick={()=>setFrameNo(0)}>
              Start Over
            </Button>
            <Button variant="primary" onClick={()=>setFrameNo(3)}>
              Next
            </Button>
          </div>
        );
      };
  return <div>Frame2A</div>;
}

export default Frame2A;
