import React from "react";
import useLcm from "../Hooks/useLcm";
import Fraction from "../Components/Fraction";

function Frame1({ num1, denom1, num2, denom2 }) {
  const lcm = useLcm(denom1, denom2);
  return (
    <div>
      <div className="d-flex flex-column justify-content-center align-items-center">
        <div className="outer-grid">
          <div>
            <h1>
              {(num1 * lcm(denom1, denom2)) / denom1}/{lcm(denom1, denom2)}
            </h1>
          </div>
          <Fraction
            num={(num1 * lcm(denom1, denom2)) / denom1}
            denom={lcm(denom1, denom2)}
            color={"blue"}
          />
        </div>
        <h1>+</h1>
        <div className="outer-grid">
          <div>
            <h1>
              {(num2 * lcm(denom1, denom2)) / denom2}/
              {(num2 * lcm(denom1, denom2)) / denom2}
            </h1>
          </div>
          <Fraction
            num={(num2 * lcm(denom1, denom2)) / denom2}
            denom={lcm(denom1, denom2)}
            color={"yellow"}
          />
        </div>
      </div>
    </div>
  );
}

export default Frame1;
