import React, { useEffect, useState } from "react";
import { Button } from "react-bootstrap";
import Fraction from "../Components/Fraction";

function Frame2A({ num1, denom1, num2, denom2,lcm , setFrameNo}) {
    const [step, setStep] = useState(0);
    const [newNum1, setNewNum1] = useState('?');
    const [newNum2, setNewNum2] = useState('?');
    useEffect(() => {
      //Implementing the setInterval method
      const interval = setInterval(() => {
        setStep(step + 1);
        if (step >= 20) {
          clearInterval(interval);
        }
      }, 1000);

      //Clearing the interval
      return () => clearInterval(interval);
    }, [step]);
    const textA = [
        `LCM of ${denom1} and ${denom2} is ${lcm}.`,
        `${num1}/${denom1} + ${num2}/${denom2} =${newNum1}/${lcm} + ${newNum2}/${lcm}.`,
        `Now, we need to find the numerators.`,
        ``,
      ];
    const findNewNum1 = () => {
        setNewNum1(num1 * (lcm / denom1));
    }
    const findNewNum2 = () => {
        setNewNum2(num2 * (lcm / denom2));
    }
    const renderButtons = () => {
        return (
          <div className="d-flex justify-content-evenly w-100">
            <Button variant="primary" onClick={()=>setFrameNo(1)}>
              Back
            </Button>
            <Button variant="primary" onClick={()=>setFrameNo(0)}>
              Start Over
            </Button>
            <Button variant="primary" onClick={()=>setFrameNo(3)}>
              Next
            </Button>
          </div>
        );
      };
  return <div>
     {step >= 1 && (
            <p className="appear  fs-2 text-left">{textA[0]}</p>
          )}
          {step >= 2 && (
            <p className="appear  fs-2 text-left">{textA[1]}</p>
          )}
          {step >= 3 && (
            <p className="appear  fs-2 text-left">{textA[2]}</p>
          )}
          {step >= 4 && (
            <div className="outer-grid appear mb-3">
            <div>
              <h1>
                {num1}/{denom1}
              </h1>
            </div>
            <Fraction num={num1} denom={denom1} color={"blue"} />
          </div>
          )}
          {step >= 5 && (
              <h1 className="text-center">
              <i className="fa-solid fa-arrow-down"></i>
              </h1>
          )}
          {step >= 6 && (
            <div className="outer-grid appear mb-3">
            <div>
              <h1>
                {newNum1}/{lcm}
              </h1>
            </div>
            <Fraction className="appear" num={num1} denom={denom1} split={lcm/denom1} color={"blue"} />
          </div>
          )}
          {step >= 7 && (
            <div className="outer-grid appear mb-3">
              <h1 className="text-center">
              and
              </h1>
          </div>
          )}
          {step >= 8 && (
            <div className="outer-grid appear mb-3">
            <div>
              <h1>
                {num2}/{denom2}
              </h1>
            </div>
            <Fraction num={num2} denom={denom2} color={"yellow"} />
          </div>
          )}
          {step >= 9 && (
              <h1 className="text-center">
              <i className="fa-solid fa-arrow-down"></i>
              </h1>
          )}
{step >= 9 && (
            <div className="outer-grid appear mb-3">
            <div>
              <h1>
                {newNum2}/{lcm}
              </h1>
            </div>
            <Fraction className="appear" num={num1} denom={denom1} split={lcm/denom1} color={"yellow"} />
          </div>
          )}


          {step >= 4 && renderButtons()}
  </div>;
}

export default Frame2A;
