import { useState, useEffect } from "react";

const useLcm = (a, b) => {
  const [lcm, setLcm] = useState(null);

  useEffect(() => {
    const calculateLcm = (a, b) => {
      if (a === 0 || b === 0) {
        setLcm(0);
        return;
      }
      const gcd = (a, b) => {
        for (let temp; b !== 0; ) {
          temp = b;
          b = a % b;
          a = temp;
        }
        return a;
      };

      const gcdValue = gcd(a, b);
      setLcm((a * b) / gcdValue);
    };

    calculateLcm(a, b); // Invoke the function
  }, [a, b]);

  return lcm;
};

export default useLcm;
