import React, { useEffect, useState } from "react";
import { Button } from "react-bootstrap";
import Fraction from "../Components/Fraction";

function Frame3({ num1, denom1, num2, denom2, lcm, setFrameNo }) {
  const [step, setStep] = useState(0);
  useEffect(() => {
    //Implementing the setInterval method
    const interval = setInterval(() => {
      setStep(step + 1);
      if (step >= 20) {
        clearInterval(interval);
      }
    }, 1000);

    //Clearing the interval
    return () => clearInterval(interval);
  }, [step]);
  const renderButtons = () => {
    return (
      <div className="d-flex w-100 justify-content-evenly appear">
        <Button
          variant="primary"
          onClick={denom1 == denom2 ? () => setFrameNo(1) : () => setFrameNo(2)}
        >
          <h2>Back</h2>
        </Button>
        <Button variant="primary" onClick={() => setFrameNo(0)}>
          <h2>Start Over</h2>
        </Button>
      </div>
    );
  };
  return (
    <div className="frame3">
      {step >= 0 && (
        <>
          <div className="outer-grid appear mb-3">
            <div>
              <h1>
                {(num1 * lcm) / denom1}/{lcm}
              </h1>
            </div>
            <Fraction
              className="appear"
              num={num1}
              denom={denom1}
              split={lcm / denom1}
              color={"blue"}
            />
          </div>
          <h1 className="text-center">+</h1>
          <div className="outer-grid appear mb-3">
            <div>
              <h1>
                {(num2 * lcm) / denom2}/{lcm}
              </h1>
            </div>
            <Fraction
              className="appear"
              num={num2}
              denom={denom2}
              split={lcm / denom2}
              color={"yellow"}
            />
          </div>
        </>
      )}

      {step >= 1 && (
        <>
          <h1 className="text-center">+</h1>

          <div className="outer-grid appear mb-3">
            <div>
              <h1>
                {(num2 * lcm) / denom2}/{lcm}
              </h1>
            </div>
            <Fraction
              className="appear"
              num={num1}
              denom={denom1}
              split={lcm / denom1}
              color={"blue"}
            />
          </div>
        </>
      )}
      {step >= 4 && renderButtons()}
    </div>
  );
}

export default Frame3;
