import { useState } from "react";
import "./App.css";
import Fraction from "./Components/Fraction";
import { Button } from "react-bootstrap";

function App() {
  const [num1, setNum1] = useState(1);
  const [num2, setNum2] = useState(2);
  const [denom1, setDenom1] = useState(1);
  const [denom2, setDenom2] = useState(3);
  const [frameNo, setFrameNo] = useState(1);
  const [valueEntered, setValueEntered] = useState(false);
  const handleSetValue = (value, setValue, nonZero) => {
    if (nonZero && value == 0) {
      setValue(1);
    } else {
      setValue(Math.floor(value));
    }
  };
  function gcd(a, b) {
    for (let temp = b; b !== 0; ) {
      b = a % b;
      a = temp;
      temp = b;
    }
    return a;
  }
  function lcm(a, b) {
    const gcdValue = gcd(a, b);
    console.log("gcdValue", gcdValue);
    console.log("a * b / gcdValue", (a * b) / gcdValue);
    return (a * b) / gcdValue;
  }
  const renderResult1 = (pic) => {
    if (pic) {
      return (
        <div className="d-flex flex-column justify-content-center align-items-center">
          <div className="outer-grid">
            <div>
              <h1>
                {(num1 * lcm(denom1, denom2)) / denom1}/{lcm(denom1, denom2)}
              </h1>
            </div>
            <Fraction
              num={(num1 * lcm(denom1, denom2)) / denom1}
              denom={lcm(denom1, denom2)}
              color={"blue"}
            />
          </div>
          <h1>+</h1>
          <div className="outer-grid">
            <div>
              <h1>
                {(num2 * lcm(denom1, denom2)) / denom2}/
                {(num2 * lcm(denom1, denom2)) / denom2}
              </h1>
            </div>
            <Fraction
              num={(num2 * lcm(denom1, denom2)) / denom2}
              denom={lcm(denom1, denom2)}
              color={"yellow"}
            />
          </div>
        </div>
      );
    } else {
      return (
        <>
          <div className="d-flex flex-column justify-content-center align-items-center ">
            <h1>{(num1 * lcm(denom1, denom2)) / denom1}</h1>
            <hr className="border border-3 border-black opacity-100 w-100" />
            <h1>{lcm(denom1, denom2)}</h1>
          </div>
          <h1>+</h1>
          <div className="d-flex flex-column justify-content-center align-items-center ">
            <h1>{(num2 * lcm(denom1, denom2)) / denom2}</h1>
            <hr className="border border-3 border-black opacity-100 w-100" />
            <h1>{lcm(denom1, denom2)}</h1>
          </div>
        </>
      );
    }
  };

  return (
    <div className="App">
      <h1 className="text-center py-3">Addition of fractions</h1>

      <div className="d-flex justify-content-center align-items-center">
        <div className="d-flex flex-column">
          <input
            type="number"
            className=" form-control small-input text-center fs-1"
            min={1}
            value={num1}
            onChange={(e) => handleSetValue(e.target.value, setNum1, true)}
            placeholder="0"
          />
          <hr className="border border-3 border-black opacity-100 mx-2" />
          <input
            type="number"
            className=" form-control small-input text-center fs-1"
            min={1}
            value={denom1}
            onChange={(e) => handleSetValue(e.target.value, setDenom1, true)}
            placeholder="1"
          />
        </div>
        <h1>+</h1>
        <div className="d-flex flex-column ">
          <input
            type="number"
            className=" form-control small-input text-center fs-1"
            min={1}
            value={num2}
            onChange={(e) => handleSetValue(e.target.value, setNum2, true)}
            placeholder="0"
          />
          <hr className="border border-3 border-black opacity-100 mx-2" />
          <input
            type="number"
            className=" form-control small-input text-center fs-1"
            min={1}
            value={denom2}
            onChange={(e) => handleSetValue(e.target.value, setDenom2, true)}
            placeholder="1"
          />
        </div>
        <Button>Go</Button>
      </div>
    </div>
  );
}

export default App;
