function gcd(a, b) {
  for (let temp; b !== 0; ) {
    temp = b;
    b = a % b;
    a = temp;
  }
  return a;
}
const useLcm = (a, b) => {
  if (a === 0 || b === 0) {
    return 0;
  }
  const gcdValue = gcd(a, b);
  return (a * b) / gcdValue;
};
export default useLcm;
