import React from "react";
import Fraction from "../Components/Fraction";
import useTypewriter from "../Hooks/useTypewritter";

function Frame1({ num1, denom1, num2, denom2,lcm }) {
  const text1A=[`Both ${num1}/${denom1} and ${num2}/${denom2} have different denominators.`,
  ` So we need to find the common denominator.`,
  ` For that, we need to find the least common multiple(LCM) of both denominators.`]
  const text1B=useTypewriter(`Both ${num1}/${denom1} and ${num2}/${denom2} have the same denominator. So we can easily add them.`, 100);
  return (
    <>
      {/* <div className="d-flex flex-column justify-content-center align-items-center"> */}
        <div className="outer-grid">
          <div>
            <h1>
              {num1}/{denom1}
            </h1>
          </div>
          <Fraction
            num={num1}
            denom={denom1}
            color={"blue"}
          />
        </div>
        <h1>+</h1>
        <div className="outer-grid">
          <div>
            <h1>
              {num2}/
              {denom2}
            </h1>
          </div>
          <Fraction
            num={num2}
            denom={denom2}
            color={"yellow"}
          />
        </div>
        <p className="hand written fs-2text-left">{text1A}</p>
      </>
    // </div>
  );
}

export default Frame1;
