import { useState } from 'react';
import './App.css';
import Fraction from './Components/Fraction';
import { Button } from 'react-bootstrap';

function App() {
  const [num1, setNum1] = useState(1)
  const [num2, setNum2] = useState(2)
  const [denom1, setDenom1] = useState(1)
  const [denom2, setDenom2] = useState(3)
  const [showResult1, setShowResult1] = useState(false)
  const [showResult2, setShowResult2] = useState(false)
  const handleSetValue = (value, setValue, nonZero) => {
    if (nonZero && value == 0) {
      setValue(1)
    }
    else { setValue(Math.floor(value)) }
    setShowResult1(false)
    setShowResult2(false)
  }
  function lcm(a, b) {
    function gcd(a, b) {
      for (let temp = b; b !== 0;) {
        b = a % b;
        a = temp;
        temp = b;
      }
      return a;
    }
    const gcdValue = gcd(a, b);
    console.log('gcdValue', gcdValue);
    console.log('a * b / gcdValue', a * b / gcdValue);
    return (a * b) / gcdValue;
  }
  const renderResult1 = (pic) => {
    if (pic) {
      return <div className='d-flex flex-column justify-content-center align-items-center'>
        <div className='outer-grid'>
      <div><h1>{num1 * (lcm(denom1, denom2)) / denom1}/{lcm(denom1, denom2)}</h1></div><Fraction num={num1 * (lcm(denom1, denom2)) / denom1} denom={lcm(denom1, denom2)} color={'blue'} /></div>
        <h1>+</h1>
        <div className='outer-grid'>
      <div><h1>{num2 * (lcm(denom1, denom2)) / denom2}/{num2 * (lcm(denom1, denom2)) / denom2}</h1></div><Fraction num={num2 * (lcm(denom1, denom2)) / denom2} denom={lcm(denom1, denom2)} color={'yellow'} /></div>
{renderButton(showResult2,setShowResult2,true)}
      </div>
    }
    else {
      return <>
        <div className='d-flex flex-column justify-content-center align-items-center '>
          <h1>{num1 * (lcm(denom1, denom2)) / denom1}</h1>
          <hr className='border border-3 border-black opacity-100 w-100' />
          <h1>{lcm(denom1, denom2)}</h1>
        </div>
        <h1>+</h1>
        <div className='d-flex flex-column justify-content-center align-items-center '>
          <h1>{num2 * (lcm(denom1, denom2)) / denom2}</h1>
          <hr className='border border-3 border-black opacity-100 w-100' />
          <h1>{lcm(denom1, denom2)}</h1>
        </div>
{renderButton(showResult2,setShowResult2,false)}
      </>
    }
  }
  const renderResult2 = (pic) => {
    if (pic) {
      return <div className='d-flex flex-column justify-content-center align-items-center'>
        <div className='outer-grid'>
          <h1>{(num1 * (lcm(denom1, denom2)) / denom1)+(num2 * (lcm(denom1, denom2)) / denom2)}/{lcm(denom1, denom2)}</h1>

        <Fraction num={(num1 * (lcm(denom1, denom2)) / denom1)+(num2 * (lcm(denom1, denom2)) / denom2)} denom={lcm(denom1, denom2)} color={'green'} />
        </div>
        </div>
    }
    else {
      return <>
        <div className='d-flex flex-column justify-content-center align-items-center '>
          <h1>{(num1 * (lcm(denom1, denom2)) / denom1)+(num2 * (lcm(denom1, denom2)) / denom2)}</h1>
          <hr className='border border-3 border-black opacity-100 w-100' />
          <h1>{lcm(denom1, denom2)}</h1>
        </div>
      </>
    }
  }
  const renderButton = (showResult,setShowResult,pic) => {
    return <Button variant='primary' className='px-3 mx-3' onClick={() => setShowResult(true)} disabled={showResult}> <h1>=</h1></Button>
  }

  return (
    <div className="App">
      <h1 className='text-center py-3'>Addition of fractions</h1>

      <div className='d-flex justify-content-center align-items-center'>
        <div className='d-flex flex-column'>
          <input type="number" className=' form-control small-input text-center fs-1' min={0} value={num1} onChange={(e) => handleSetValue(e.target.value, setNum1, false)} placeholder="0" />
          <hr className='border border-3 border-black opacity-100 mx-2' />
          <input type="number" className=' form-control small-input text-center fs-1' min={1} value={denom1} onChange={(e) => handleSetValue(e.target.value, setDenom1, true)} placeholder="1" />
        </div>
        <h1>+</h1>
        <div className='d-flex flex-column '>
          <input type="number" className=' form-control small-input text-center fs-1' min={0} value={num2} onChange={(e) => handleSetValue(e.target.value, setNum2, false)} placeholder="0" />
          <hr className='border border-3 border-black opacity-100 mx-2' />
          <input type="number" className=' form-control small-input text-center fs-1' min={1} value={denom2} onChange={(e) => handleSetValue(e.target.value, setDenom2, true)} placeholder="1" />
        </div>
        {renderButton(showResult1,setShowResult1,false)}
        {showResult1 && renderResult1(false)}
        {showResult2 && renderResult2(false)}


      </div>
      <div className='d-flex flex-column justify-content-center align-items-center'>
      <div className='outer-grid'>
      <div><h1>{num1}/{denom1}</h1></div><Fraction num={num1} denom={denom1 ? denom1 : 1} color={'blue'} /></div>
        <h1 className='m-1'>+</h1>
        <div className='outer-grid'>
      <div><h1>{num2}/{denom2}</h1></div> <Fraction num={num2} denom={denom2 ? denom2 : 1} color={'yellow'} /></div>
        {renderButton(showResult1,setShowResult1,true)}
      </div>
      {showResult1 && renderResult1(true)}
      {showResult2 && renderResult2(true)}

    </div>
  );
}

export default App;
